import axios from "axios";

export const GET_INVENTORY_LIST = "GET_INVENTORY_LIST";
export const GET_INVENTORY_DETAIL = "GET_INVENTORY_DETAIL";
export const POST_INVENTORY_CREATE = "POST_INVENTORY_CREATE";
export const PUT_INVENTORY_EDIT = "PUT_INVENTORY_EDIT";
export const POST_USER_LOGIN = "POST_USER_LOGIN";
 
export const getInventoryList = () => {
  return (dispatch) => {
    axios
      .get("https://my-json-server.typicode.com/aliefian9/FakeApiTypecode/db/")
      .then(function (response) {
        dispatch({
          type: GET_INVENTORY_LIST,
          payload: {
            data: response.data.inventory,
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: GET_INVENTORY_LIST,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};

export const getInventoryDetail = (id) => {
  return (dispatch) => {
    axios
      .get(
        "https://my-json-server.typicode.com/aliefian9/FakeApiTypecode/inventory/" +
          id
      )
      .then(function (response) {
        dispatch({
          type: GET_INVENTORY_DETAIL,
          payload: {
            data: response.data,
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: GET_INVENTORY_DETAIL,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};

export const postInventoryCreate = (data) => {
  return (dispatch) => {
    axios
      .post(
         "https://my-json-server.typicode.com/aliefian9/FakeApiTypecode/inventory/",
        data
      )
      .then(function (response) {
        console.log(response);
        
        dispatch({
          type: POST_INVENTORY_CREATE,
          payload: {
            data: response.data,
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: POST_INVENTORY_CREATE,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      }); 
  };
};

export const putInventoryUpdate = (data, id) => {
  return (dispatch) => {
    axios
      .put(
         "https://my-json-server.typicode.com/aliefian9/FakeApiTypecode/inventory/"+id,
        data
      )
      .then(function (response) {
        console.log(response);
        
        dispatch({
          type: PUT_INVENTORY_EDIT,
          payload: {
            data: response.data,
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: PUT_INVENTORY_EDIT,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};


export const deleteInventory = (id) => {
  return (dispatch) => {
    axios
      .delete(
         "https://my-json-server.typicode.com/aliefian9/FakeApiTypecode/inventory/"+id
      )
      .then(function (response) {
        console.log(response);
        
      })
      .catch(function (error) {
        console.log(error);
        
      });
  };
};


export const deleteDataInventory = () => {
  return (dispatch) => {
    dispatch({
      type: GET_INVENTORY_DETAIL,
      payload: {
        data: false,
        errorMessage: false,
      },
    });


    dispatch({
      type: POST_INVENTORY_CREATE,
      payload: {
        data: false,
        errorMessage: false,
      },
    });
  };
};

export const postUserLogin = (data) => {
  return (dispatch) => {
    axios
      .post(
         "https://my-json-server.typicode.com/aliefian9/FakeApiTypecode/db"
      )
      .then(function (response) {
        
        dispatch({
          type: POST_USER_LOGIN,
          payload: {
            data: localStorage.setItem('token', response.data.profile.token),
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: POST_USER_LOGIN,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};


