import React, { Component } from "react";
import NavbarComponent from "./Components/NavbarComponent";
import TableComponent from "./Components/TableComponent";
import HomeContainer from "./Container/HomeContainer";
import DetailContainer from "./Container/DetailContainer";
import EditContainer from "./Container/EditContainer";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

export default class App extends Component {
  state = {
    title: "PERPASIP FRONT END",
    barang: [
      {
        id: 1,
        logical_uid: "834e7f03",
        name: "RX-0 Unicorn Gundam",
        qty: 10
      },
      {
        id: 2,
        logical_uid: "834e7f03-1",
        name: "RX-78-2 Gundam",
        qty: 100
      },
    ],
  };

  render() {
    return (
      <div>
        <NavbarComponent />
        <Router>
          <Switch>
            <Route path="/Table">
              <TableComponent barang={this.state.barang}/>
            </Route>
            <Route path="/Barang" exact>
              <BarangContainer />
            </Route>
            <Route path="/detail/:id" exact>
              <DetailContainer />
            </Route>
            <Route path="/edit/:id" exact>
              <EditContainer />
            </Route>
            <Route path="/" exact>
              <HomeContainer />
            </Route>
          </Switch>
        </Router>
      </div>
    )
  }
}
