import axios from "axios";

export const GET_BARANG_LIST = "GET_BARANG_LIST";
export const GET_ANALITIK_LIST = "GET_ANALITIK_LIST";


export const getBarangList = () => {
  return (dispatch) => {
    axios
      .get("http://smpnapi.herokuapp.com/api/inventory/")
      .then(function (response) {
          dispatch({
            type: GET_BARANG_LIST,
            payload: {
              data: response.data.Inventory,
              errorMessage: false,
            },
          });
      })
      .catch(function (error) {
        dispatch({
          type: GET_BARANG_LIST,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};


export const getAnalitikList = () => {
  return (dispatch) => {
    axios
      .get("http://smpnapi.herokuapp.com/api/logging/")
      .then(function (response) {
          dispatch({
            type: GET_ANALITIK_LIST,
            payload: {
              data: response.data.logging,
              errorMessage: false,
            },
          });
      })
      .catch(function (error) {
        dispatch({
          type: GET_ANALITIK_LIST,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};


