import axios from "axios";

export const GET_BARANG_LIST = "GET_BARANG_LIST";
export const GET_ANALITIK_LIST = "GET_ANALITIK_LIST";


export const getBarangList = () => {
  return (dispatch) => {
    axios
      .get("https://smpnapi.herokuapp.com/api/v3/inventory/")
      .then(function (response) {
          dispatch({
            type: GET_BARANG_LIST,
            payload: {
              data: response.data.Inventory,
              errorMessage: false,
            },
          });
      })
      .catch(function (error) {
        dispatch({
          type: GET_BARANG_LIST,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};


export const getAnalitikList = () => {
  let url = "https://smpnapi.herokuapp.com/api/v3/logging/";
  return (dispatch) => {
    axios
      .get(url)
      .then(function (response) {
          dispatch({
            type: GET_ANALITIK_LIST,
            payload: {
              data: response.data.logging,
              errorMessage: false,
            },
          });
      })
      .catch(function (error) {
        dispatch({
          type: GET_ANALITIK_LIST,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};


