import React, { Component } from "react";
import { Container } from "reactstrap";
import BackComponent from "../components/BackComponent";
import { connect } from "react-redux";
import FormComponent from "../components/FormComponent";
import { getInventoryDetail, putInventoryUpdate } from "../actions/inventoryAction";
import swal from "sweetalert";

const mapStateToProps = (state) => {
  return {
    getResponDataInventory: state.inventorys.getResponDataInventory,
    errorResponDataInventory: state.inventorys.errorResponDataInventory,
  };
};

class EditInventoryContainer extends Component {
  componentDidMount() {
    this.props.dispatch(getInventoryDetail(this.props.match.params.id));
  }

  handleSubmit(data) {
    this.props.dispatch(putInventoryUpdate(data, this.props.match.params.id));
  }

  render() {
    if (this.props.getResponDataInventory || this.props.errorResponDataInventory) {
      if (this.props.errorResponDataInventory) {
        swal("Failed!", this.props.errorResponDataInventory, "error");
      } else {
        swal(
          "Inventory Updated!",
          "Nama : " +
            this.props.getResponDataInventory.nama +
            " , Umur : " +
            this.props.getResponDataInventory.umur,
          "success"
        );
      }
    }
    return (
      <Container>
        <BackComponent />
        <h1>Edit Inventory</h1>
        <FormComponent onSubmit={(data) => this.handleSubmit(data)} />
      </Container>
    );
  }
}

export default connect(mapStateToProps, null)(EditInventoryContainer);
