import React, { Component } from "react";
// import { connect } from "react-redux";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

import NavbarComponent from "./components/NavbarComponent";
import HomeContainer from "./containers/HomeContainer";
import CreateInventoryContainer from "./containers/CreateInventoryContainer";
import EditInventoryContainer from "./containers/EditInventoryContainer";
import DetailInventoryContainer from "./containers/DetailInventoryContainer";
import DataContainer from "./containers/DataContainer";

import LoginContainer from "./containers/LoginContainer";
import AnalitikInventoryContainer from "./containers/AnalitikInventoryContainer";
import Logout from "./containers/Logout";
import my404 from "./containers/my404";

// const mapStateToProps = (state) => {
//   return {
//     getUserLogin: state.inventorys.getUserLogin,
//     errorUserLogin: state.inventorys.errorUserLogin,
//   };
// };

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      logged_in: true,
    };
  }

  render() {
    if (localStorage.getItem("token") == null) {
      return (
        <div>
          <Router>
            <Switch>
            <Route path="/" exact component={LoginContainer} />
            <Route path='*' component={my404} />

          </Switch></Router>
        </div>
      );
    } else {
      return (
        <div>
          <NavbarComponent />
          <Router>
            <Switch>
              <Route path="/Home" exact component={HomeContainer} />
              <Route
                path="/Create"
                exact
                component={CreateInventoryContainer}
              />
              <Route
                path="/Detail/:id"
                exactcomponent={DetailInventoryContainer}
              />
              <Route
                path="/Edit/:id"
                exact
                component={EditInventoryContainer}
              />
              <Route path="/Data" exact component={DataContainer} />
              <Route
                path="/Analitik"
                exact
                component={AnalitikInventoryContainer}
              />
              <Route path="/Logout" exact component={Logout} />
              <Route path='*' exact={true} component={my404} />
            </Switch>
          </Router>
        </div>
      );
    }
  }
}

export default App;
