import axios from "axios";

export const GET_BARANG_LIST = "GET_BARANG_LIST";

export const getBarangList = () => {
  return (dispatch) => {
    axios
      .get("https://my-json-server.typicode.com/afifbasya/reactjs-redux/users")
      .then(function (response) {
          // dispatch({
          //   type: GET_BARANG_LIST,
          //   payload: {
          //     data: response.data.Inventory,
          //     errorMessage: false,
          //   },
          // });
          console.log(response);
      })
      .catch(function (error) {
        dispatch({
          type: GET_BARANG_LIST,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};
