import { GET_BARANG_LIST, GET_ANALITIK_LIST } from "../Action/BarangAction";

let initialState = {
  getBarangList: false,
  errorBarangList: false,
  getAnalitikList: false,
  errorAnalitikList: false,
  // getDetailList: false,
  // errorDetailList: false,
};

const barangInventory = (state = initialState, action) => {
  switch (action.type) {
    case GET_BARANG_LIST:
      return {
        ...state,
        getBarangList: action.payload.data,
        errorBarangList: action.payload.errorMessage,
      };
    case GET_ANALITIK_LIST:
      return {
        ...state,
        getAnalitikList: action.payload.dataAnalitik,
        errorAnalitikList: action.payload.errorMessage,
      };
    case GET_DETAIL_LIST:
      return {
        ...state,
        getDetailList: action.payload.dataDetail,
        errorDetailList: action.payload.errorMessage,
      };
    default:
      return state;
  }
};

export default barangInventory;
