import {
  GET_INVENTORY_LIST,
  GET_INVENTORY_DETAIL,
  POST_INVENTORY_CREATE,
  PUT_INVENTORY_EDIT,
} from "../actions/inventoryAction";

let initialState = {
  getUsersList: false,
  errorUsersList: false,
  getUserDetail: false,
  errorUserDetail: false,
  getResponDataUser: false,
  errorResponDataUser: false,
  title: "Wahidev Academy",
};

const users = (state = initialState, action) => {
  switch (action.type) {
    case GET_INVENTORY_LIST:
      return {
        ...state,
        getUsersList: action.payload.data,
        errorUsersList: action.payload.errorMessage,
      };

    case GET_INVENTORY_DETAIL:
      return {
        ...state,
        getUserDetail: action.payload.data,
        errorUserDetail: action.payload.errorMessage,
      };

    case POST_INVENTORY_CREATE:
      return {
        ...state,
        getResponDataUser: action.payload.data,
        errorResponDataUser: action.payload.errorMessage,
      };

    case PUT_INVENTORY_EDIT:
      return {
        ...state,
        getResponDataUser: action.payload.data,
        errorResponDataUser: action.payload.errorMessage,
      };

    default:
      return state;
  }
};

export default users;
