import axios from "axios";

export const GET_BARANG_LIST = "GET_BARANG_LIST";

export const getBarangList = () => {
  return (dispatch) => {
    axios
      .get("http://smpnapi.herokuapp.com/api/inventory/")
      //      .get("https://my-json-server.typicode.com/afifbasya/reactjs-redux/users")
      .then(function (response) {
        console.log(response.data);
        console.log(response.status);
        console.log(response.statusText);
        console.log(response.headers);
        console.log(response.config);

        // .then(function (response) {
        //   dispatch({
        //     type: GET_BARANG_LIST,
        //     payload: {
        //       data: response.data,
        //       errorMessage: false,
        //     },
        //   });
        // console.log(response);
      })
      .catch(function (error) {
        dispatch({
          type: GET_BARANG_LIST,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};
