import axios from "axios";

export const GET_BARANG = "GET_BARANG";

export const getBarang = () => {
  return (dispatch) => {
    axios
      .get("https://my-json-server.typicode.com/afifbasya/reactjs-redux/users")
      .then(function (response) {
        dispatch({
          type: GET_BARANG,
          payload: {
            data: response.data,
            errorMessage: false,
          }
        })
      })
      .catch(function (error) {
        dispatch({
          type: GET_BARANG,
          payload: {
            data: false,
            errorMessage: error.message,
          }
        })
      })
  };
};
