import axios from "axios";

export const GET_BARANG_LIST = "GET_BARANG_LIST";
export const GET_ANALITIK_LIST = "GET_ANALITIK_LIST";
export const GET_DETAIL_LIST = "GET_DETAIL_LIST";
// let url = "https://smpnapi.herokuapp.com/api/v3/";
let url = "https://my-json-server.typicode.com/afifbasya/reactjs-redux/users";

export const getBarangList = () => {
  return (dispatch) => {
    axios
      // .get(url + "inventory")
      .get(url)
      .then(function (response) {
        dispatch({
          type: GET_BARANG_LIST,
          payload: {
            // data: response.data.Inventory,
            dataBarang: response.data,
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: GET_BARANG_LIST,
          payload: {
            dataBarang: false,
            errorMessage: error.message,
          },
        });
      });
  };
};

export const getAnalitikList = () => {
  return (dispatch) => {
    axios
      // .get(url + "logging")
      .get(url)
      .then(function (response) {
        dispatch({
          type: GET_ANALITIK_LIST,
          payload: {
            // dataAnalitik: response.data.logging,
            dataAnalitik: response.data,
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: GET_ANALITIK_LIST,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};

export const getDetailList = (id) => {
  return (dispatch) => {
    axios
     // eslint-disable-next-line
      .get(url + "detail/" + id)
      .then(function (response) {
        dispatch({
          type: GET_DETAIL_LIST,
          payload: {
            dataDetail: response.data,
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: GET_DETAIL_LIST,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};
