import React, { Component } from "react";

export default class Login extends Component {

    state = {
        credentials : {uname: '', passwd: ''}
    }

    login = event => {
        console.log(this.state.credentials);
        fetch('http://smpnapi.herokuapp.com/api/v3/users/',{
        method : 'POST',
        Headers:
         {  ' Accept' : 'application/json',
            'Content-Type': 'application/json'},
        body: JSON.stringify(this.state.credentials)
        })
    .then(function (response) {
      console.log(response.data.user.uname);
    })
        .catch( error => console.error(error))
    }

    inputChanged = event => {
        const cred = this.state.credentials;
        cred[event.target.name] = event.target.value;
        this.setState({credentials: cred});
    }
    
    render() {
        return (
            <form>
                <h3>Login User</h3>

                <div className="form-group">
                    <label>Username</label>
                    <input type="text" className="form-control" placeholder="Enter username" name="uname"
                    value={this.state.credentials.uname}
                    onChange={this.inputChanged}/>
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" name="passwd" 
                    value={this.state.credentials.passwd}
                    onChange={this.inputChanged}/>
                </div>

                <button type="submit" className="btn btn-primary btn-block" onClick={this.login}>Login</button>
            </form>
        );
    }

}
