import { GET_BARANG } from "../Action/BarangAction";
let initialState = {
  getBarang: false,
  getError: false,
};

const Data = (state = initialState, action) => {
  switch (action.type) {
    case GET_BARANG:
      return {
        ...state,
        getBarang: action.payload.data,
        getError: action.payload.errorMessage,
      };
    default:
      return state;
  }
};

export default Data;
