import axios from "axios";

export const GET_BARANG = "GET_BARANG";

export const getBarang = () => {
  return (dispatch) => {
    axios
      .get("http://smpnapi.herokuapp.com/api/inventory/")
      .then(function (response) {
        dispatch({
          type: GET_BARANG,
          payload: {
            data: response.data.Inventory,
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: GET_BARANG,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};
