import React, { Component } from "react";
import { reduxForm, Field } from "redux-form";
import { connect } from "react-redux";
import { FormGroup, Col, Label, Input, Row, Button } from "reactstrap";
import InventoryValidation from "../validations/InventoryValidation";

const renderField = ({
  input,
  type,
  placeholder,
  label,
  disabled,
  readOnly,
  meta: { touched, error, warning },
}) => (
  <Row>
    <Col md="12">
      <Label htmlFor="{input}" className="col-form-label">
        {label}
      </Label>
    </Col>
    <Col md="12">
      <Input
        {...input}
        type={type}
        placeholder={placeholder}
        disabled={disabled}
        readOnly={readOnly}
      ></Input>
      {touched &&
        ((error && <p style={{ color: "red" }}>{error}</p>) ||
          (warning && <p style={{ color: "brown" }}>{warning}</p>))}
    </Col>
  </Row>
);

const mapStateToProps = (state) => {
  return {
    initialValues : {
      name : state.inventorys.getInventoryDetail.name,
      qty : state.inventorys.getInventoryDetail.qty,
    }
  };
};

const formdata = () => (
  <form onSubmit={this.props.handleSubmit}>
  <FormGroup row>
    <Col md={6}>
      <FormGroup>
        <Field
          type="text"
          name="name"
          component={renderField}
          label="Nama :"
        />
      </FormGroup>
    </Col>

    <Col md={6}>
      <FormGroup>
        <Field
          type="text"
          name="qty"
          component={renderField}
          label="Jumlah :"
        />
      </FormGroup>
    </Col>
  </FormGroup>

  <FormGroup row>
    <Col md="12">
      <FormGroup>
        <Button
          color="dark"
          type="submit"
          disabled={this.props.submitting}
        >
          Submit
        </Button>
      </FormGroup>
    </Col>
  </FormGroup>
</form>
)

class FormComponent extends Component {
  render() {
    return (
      <div>
        {formdata}
      </div>
    );
  }
}

FormComponent = reduxForm({
  form: "formCreateInventory",
  validate: InventoryValidation,
  enableReinitialize: true,
})(FormComponent);
export default connect(mapStateToProps, null)(FormComponent);
