import React, { Component } from "react";
import NavbarComponent from "./components/NavbarComponent";
// import JumbotronComponent from "./components/JumbotronComponent";
import { BrowserRouter, Route } from "react-router-dom";
import HomeContainer from "./containers/HomeContainer";
import CreateInventoryContainer from "./containers/CreateInventoryContainer";
import EditInventoryContainer from "./containers/EditInventoryContainer";
import DetailInventoryContainer from "./containers/DetailInventoryContainer";
import DataContainer from "./containers/DataContainer";
import { connect } from "react-redux";
import LoginContainer from "./containers/LoginContainer";

const mapStateToProps = (state) => {
  return {
    getUserLogin: state.inventorys.getUserLogin,
    errorUserLogin: state.inventorys.errorUserLogin,
  };
};

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      logged_in:  true,
    };
  }
  

  render() {
    if (localStorage.getItem('token') == null) {
      return (
        <div>
            <BrowserRouter>
              <Route path="/" exact component={LoginContainer} />
            </BrowserRouter>
          </div>
      )
    } else {
      return (
        <div>
        <NavbarComponent />
        <BrowserRouter>
          <Route path="/" exact component={HomeContainer} />
          <Route
            path="/Create"
            exact
            component={CreateInventoryContainer}
          />
          <Route
            path="/Detail/:id"
            exactcomponent={DetailInventoryContainer}
          />
          <Route
            path="/Edit/:id"
            exact
            component={EditInventoryContainer}
          />
          <Route path="/Data" exact component={DataContainer} />
        </BrowserRouter>
      </div>
      )
    }
  }
}

export default connect(mapStateToProps, null)(App);
