import React from "react";
import { connect } from "react-redux";
import ReactApexChart from 'apexcharts'


const mapStateToProps = function (state) {
  return {
    getAnalitikList: state.barangInventory.getAnalitikList,
    errorAnalitikList: state.barangInventory.errorAnalitikList,
  };
};

this.state = {
          
    series: [{
      name: 'Net Profit',
      data: [44, 55, 57, 56, 61, 58, 63, 60, 66]
    }, {
      name: 'Revenue',
      data: [76, 85, 101, 98, 87, 105, 91, 114, 94]
    }, {
      name: 'Free Cash Flow',
      data: [35, 41, 36, 26, 45, 48, 52, 53, 41]
    }],
    options: {
      chart: {
        type: 'bar',
        height: 350
      },
      plotOptions: {
        bar: {
          horizontal: false,
          columnWidth: '55%',
          endingShape: 'rounded'
        },
      },
      dataLabels: {
        enabled: false
      },
      stroke: {
        show: true,
        width: 2,
        colors: ['transparent']
      },
      xaxis: {
        categories: ['Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct'],
      },
      yaxis: {
        title: {
          text: '$ (thousands)'
        }
      },
      fill: {
        opacity: 1
      },
      tooltip: {
        y: {
          formatter: function (val) {
            return "$ " + val + " thousands"
          }
        }
      }
    },
  
  
  };

const DataAnalitikComponent = (props) => {
  return (
    <div id="chart">
      <ReactApexChart
        options={this.state.options}
        series={this.state.series}
        type="bar"
        height={350}
      />
    </div>
  );
};

// <div>
//   {props.getAnalitikList((item, logical_uid) => {
//     return <li key={logical_uid}>{item.logical_uid}</li>;
//   })}
// </div>

export default connect(mapStateToProps, null)(DataAnalitikComponent);
