import React, { Component } from "react";

import NavbarComponent from "./Components/NavbarComponent";
import HomeContainer from "./Container/HomeContainer";
import DetailContainer from "./Container/DetailContainer";
import EditContainer from "./Container/EditContainer";
import BarangContainer from "./Container/BarangContainer";
import AnalitikContainer from "./Container/AnalitikContainer";
import CreateContainer from "./Container/CreateContainer";
import LoginContainer from "./Container/LoginContainer";

import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

export default class App extends Component {
  state = {
    title: "PERPASIP FRONT END",
    
  };

  render() {
    return (
      <div>
        <NavbarComponent />
        <Router>
          <Switch>
            <Route path="/Barang" exact>
              <BarangContainer/>
            </Route>
            <Route path="/Detail/:logical_uid" exact>
              <DetailContainer />
            </Route>
            <Route path="/Edit/:logical_uid" exact>
              <EditContainer />
            </Route>
            <Route path="/Analitik" exact>
              <AnalitikContainer />
            </Route>
            <Route path="/Create" exact>
              <CreateContainer />
            </Route>
            <Route path="/Home" exact>
              <HomeContainer />
            </Route>
            <Route path="/" exact>
              <LoginContainer />
            </Route>
          </Switch>
        </Router>
      </div>
    )
  }
}
