import React, { Component } from "react";
import axios from 'axios';
import List from "./List";
 
class FetchApi extends Component {
  
  constructor() {
    super();
    this.state = {
      data: []
    };
  }
  componentDidMount() {
    axios.get("http://localhost:8000/api/inventory/?format=json").then(res =>
      this.setState({
        data: res.data
      })
    );
  }
  render() {
    return <>{this.state.data.map((data, key) => {
    return <List judul={data.name} isi={data.qty} />
  })}</>;
  }





}


export default FetchApi;
