import { GET_BARANG_LIST, GET_ANALITIK_LIST } from "../Action/BarangAction";

let initialState = {
  getBarangList: false,
  errorBarangList: false,
  getBarangAnalitik: false,
};

const barangInventory = (state = initialState, action) => {
  switch (action.type) {
    case GET_BARANG_LIST:
      return {
        ...state,
        getBarangList: action.payload.data,
        errorBarangList: action.payload.errorMessage,
      };
      case GET_ANALITIK_LIST:
        return {
          ...state,
          getBarangAnalitik: action.payload.data,
          errorBarangAnalitik: action.payload.errorMessage,
        };
    default:
      return state;
  }
};

export default barangInventory;
