import axios from "axios";

export const GET_INVENTORY_LIST = "GET_INVENTORY_LIST";
export const GET_INVENTORY_DETAIL = "GET_INVENTORY_DETAIL";
export const POST_INVENTORY_CREATE = "POST_INVENTORY_CREATE";
export const PUT_INVENTORY_EDIT = "PUT_INVENTORY_EDIT";
export const POST_USER_LOGIN = "POST_USER_LOGIN";

let url = "https://smpnapi.herokuapp.com/api/v3/";
let header = {
  headers: {
    Authorization: `JWT ${localStorage.getItem('token')}`
  }
}

export const getInventoryList = () => {
  return (dispatch) => {
    axios
      .get(url + 'inventory/', header)
      .then(function (response) {
        dispatch({
          type: GET_INVENTORY_LIST,
          payload: {
            data: response.data.Inventory,
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: GET_INVENTORY_LIST,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};

export const getInventoryDetail = (logical_uid) => {
  return (dispatch) => {
    axios
      .get(url + 'inventory/' +  logical_uid, header)
      .then(function (response) {
        dispatch({
          type: GET_INVENTORY_DETAIL,
          payload: {
            data: response.data,
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: GET_INVENTORY_DETAIL,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};

export const postInventoryCreate = (data) => {
  return (dispatch) => {
    axios
      .post(url + 'inventory/',data,header)
      .then(function (response) {
        console.log(response);

        dispatch({
          type: POST_INVENTORY_CREATE,
          payload: {
            data: response.data,
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: POST_INVENTORY_CREATE,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};

export const deleteInventory = (logical_uid) => {
  return (dispatch) => {
    axios
      .delete(url + 'inventory/' + logical_uid, header)
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
};

export const deleteDataInventory = () => {
  return (dispatch) => {
    dispatch({
      type: GET_INVENTORY_DETAIL,
      payload: {
        data: false,
        errorMessage: false,
      },
    });

    dispatch({
      type: POST_INVENTORY_CREATE,
      payload: {
        data: false,
        errorMessage: false,
      },
    });
  };
};

export const putInventoryUpdate = (data, id) => {
  return (dispatch) => {
    axios
      .put(
        "https://my-json-server.typicode.com/aliefian9/FakeApiTypecode/inventory/" +
          id,
        data
      )
      .then(function (response) {
        console.log(response);

        dispatch({
          type: PUT_INVENTORY_EDIT,
          payload: {
            data: response.data,
            errorMessage: false,
          },
        });
      })
      .catch(function (error) {
        dispatch({
          type: PUT_INVENTORY_EDIT,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};

export const postUserLogin = () => {
  return (dispatch) => {
    axios({
      method: "post",
      url: "https://smpnapi.herokuapp.com/token-auth/",
      data: {
        username: "aliefian",
        password: "Asus1234",
      },
    });
  };
};

export const getUserLogin = () => {
  axios.get("https://smpnapi.herokuapp.com/api/v3/users/").then(function (response) {
    console.log(response.data);
    console.log(response.status);
    console.log(response.statusText);
    console.log(response.headers);
    console.log(response.config);
  });
};

