import axios from "axios";

export const GET_BARANG = "GET_BARANG";

export const getBarang = () => {
  return (dispatch) => {
    axios
      .get("http://smpnapi.herokuapp.com/api/inventory")
      .then(function (response) {
        // handle success
        console.log(response);
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      })
  };
};
