import React from "react";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";

import { Container, Button, Row, Col, Spinner } from "reactstrap";

import {
  faInfo,
  faEdit,
  faTrash,
  faPlus,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link } from "react-router-dom";
import { connect } from "react-redux";

const { SearchBar } = Search;

const defaultSorted = [
  {
    dataField: "logical_uid",
    order: "asc",
  },
];

const mapStateToProps = function (state) {
  return {
    getBarangList: state.barangInventory.getBarangList,
    errorBarangList: state.barangInventory.errorBarangList,
  };
};

const columns = [
  {
    dataField: "logical_uid",
    text: "Logical UID",
    sort: true,
    headerStyle: () => {
      return { width: "15%" };
    },
  },
  {
    dataField: "name",
    text: "Nama",
    sort: true,
  },
  {
    dataField: "qty",
    text: "Jumlah",
    sort: true,
  },
  {
    dataField: "link",
    text: "Action",
    formatter: (rowContent, row) => {
      return (
        <div>
          <Link to={"detail/" + row.logical_uid}>
            <Button color="dark" className="mr-2">
              <FontAwesomeIcon icon={faInfo} /> Detail
            </Button>
          </Link>

          <Link to={"edit/" + row.id}>
            <Button color="dark" className="mr-2">
              <FontAwesomeIcon icon={faEdit} /> Edit
            </Button>
          </Link>

          <Button color="dark" className="mr-2">
            <FontAwesomeIcon icon={faTrash} /> Delete
          </Button>
        </div>
      );
    },
  },
];

const TableComponent = (props) => {
  return (
    <Container>
      {props.getBarangList ? (
        <ToolkitProvider
          bootstrap4
          keyField="id"
          data={props.getBarangList}
          columns={columns}
          defaultSorted={defaultSorted}
          search
        >
          {(props) => (
            <div>
              <Row>
                <Col>
                  <Link to="/Create">
                    <Button color="dark" className="mr-2">
                      <FontAwesomeIcon icon={faPlus} /> Add
                    </Button>
                  </Link>
                </Col>
                <Col>
                  <div className="float-right">
                    <SearchBar {...props.searchProps} placeholder="Cari.." />
                  </div>
                </Col>
              </Row>

              <BootstrapTable
                {...props.baseProps}
                pagination={paginationFactory()}
              />
            </div>
          )}
        </ToolkitProvider>
      ) : (
        <div className="text-center">
          { props.errorBarangList ? <h1>{props.errorBarangList}</h1> : <Spinner color="dark"/>}
        </div>
      )}
    </Container>
  );
};

export default connect(mapStateToProps, null)(TableComponent);
