import React, { Component } from "react";
import NavbarComponent from "./components/NavbarComponent";
// import JumbotronComponent from "./components/JumbotronComponent";
import { BrowserRouter, Route } from "react-router-dom";
import HomeContainer from "./containers/HomeContainer";
import CreateInventoryContainer from "./containers/CreateInventoryContainer";
import EditInventoryContainer from "./containers/EditInventoryContainer";
import DetailInventoryContainer from "./containers/DetailInventoryContainer";
import DataContainer from "./containers/DataContainer";
import LoginContainer from "./containers/LoginContainer";
import { connect } from "react-redux";

const mapStateToProps = (state) => {
  return {
    getUserLogin: state.inventorys.getUserLogin,
    errorUserLogin: state.inventorys.errorUserLogin,
  };
};

class App extends Component {
  componentDidMount() {
    if (this.state.logged_in) {
      fetch("https://smpnapi.herokuapp.com/api/v3/users/current_user/", {
        headers: {
          Authorization: `JWT ${localStorage.getItem("token")}`,
        },
      })
        .then((res) => res.json())
        .then((json) => {
          this.setState({ username: json.username });
        });
    }
  }

  handle_login = (e, data) => {
    e.preventDefault();
    fetch("https://smpnapi.herokuapp.com/token-auth/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((res) => res.json())
      .then((json) => {
        localStorage.setItem("token", json.token);
        this.setState({
          getUserLogin: true,
        });
      });
  };

  handle_logout = () => {
    localStorage.removeItem("token");
    this.setState({ getUserLogin: false,});
  };

  render() {
    return (
      <div>
        <NavbarComponent />

        <BrowserRouter>
          <Route path="/Home" exact component={HomeContainer} />

          <Route path="/Create" exact component={CreateInventoryContainer} />

          <Route
            path="/Detail/:id"
            exact
            component={DetailInventoryContainer}
          />

          <Route path="/Edit/:id" exact component={EditInventoryContainer} />

          <Route path="/Data" exact component={DataContainer} />

          <Route path="/" exact component={LoginContainer} />
        </BrowserRouter>
      </div>
    );
  }
}

export default connect(mapStateToProps, null)(App);

