import BackComponent from "../components/BackComponent";
import NavbarComponent from "../components/NavbarComponent";
import { Redirect } from "react-router-dom";
import React, { Component } from "react";
import { Container } from "reactstrap";
import FormLogging from "../components/FormLogging";
import {
  putLoggingUpdate,
} from "../actions/inventoryAction";
import swal from "sweetalert";

class EditLogging extends Component {
  constructor(props) {
    super(props);
    const token = localStorage.getItem("token");

    let loggedin = true;
    if (token == null) {
      loggedin = false;
    }

    this.state = {
      loggedin,
    };
  }
  
  handleSubmit(data) {
    this.props.dispatch(
      putLoggingUpdate(data, this.props.match.params.logical_uid)
    );
  }

  render() {
    if (this.state.loggedin === false) {
      return <Redirect to="/" />;
    }
    if (
      this.props.getResponDataInventory ||
      this.props.errorResponDataInventory
    ) {
      if (this.props.errorResponDataInventory) {
        swal("Failed!", this.props.errorResponDataInventory, "error");
      } else {
        swal(
          "Logging Updated!",
        );
      }
    }
    return (
      <div>
        <NavbarComponent />
        <Container>
          <BackComponent />
          <h1>Edit Logging</h1>
          <FormLogging onSubmit={(data) => this.handleSubmit(data)} />
        </Container>
      </div>
    );
  }
}

export default EditLogging;
