import axios from "axios";

export const GET_BARANG_LIST = "GET_BARANG_LIST";

export const getBarangList = () => {
  return (dispacth) => {
    axios
      .get("http://smpnapi.herokuapp.com/api/inventory/")
      .then(function (response) {
        // handle success
        console.log(response);
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      })
      .then(function () {
        // always executed
      });
  };
};
