import axios from "axios";

export const GET_BARANG_LIST = "GET_BARANG_LIST";

export const getBarangList = () => {
  return (dispatch) => {
    axios
      .get("http://smpnapi.herokuapp.com/api/inventory/")
      .then(function (response) {
        dispatch({ type: GET_BARANG_LIST, payload: response.data });

        // dispatch({
        //   type: GET_BARANG_LIST,
        //   payload: {
        //     data: response.data.Inventory,
        //     errorMessage: false,
        //   },
        // });
        // console.log(response);
      })
      .catch(function (error) {
        dispatch({
          type: GET_BARANG_LIST,
          payload: {
            data: false,
            errorMessage: error.message,
          },
        });
      });
  };
};
